sources.style.scss:
body {
  min-height: 100vh;
  background-color: #d3e7ee;
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen,
    Ubuntu, Cantarell, "Open Sans", "Helvetica Neue", sans-serif;
  overflow: hidden;

  h1 {
    color: #7097a8;
    margin: 50px 0;
    text-align: center;
  }

  .player-wrapper {
    background-color: white;
    width: 280px;
    height: 500px;
    margin: 0 auto;
    padding: 5px;
    border-radius: 5px;

    p {
      margin: 0;
    }

    .player-inside-wrapper {
      background-color: #7097a8;
      border-radius: 5px;
      height: 100%;

      .song-image {
        padding: 20px;

        img {
          width: 100%;
          box-shadow: 0px 5px 25px rgb(50 50 50 / 50%);
        }
      }

      .player-artist-wrapper {
        text-align: center;
        margin-bottom: 30px;
        color: white;

        .song-name {
          font-size: 20px;
          font-weight: 700;
        }

        .artist-name {
          font-size: 16px;
        }
      }

      .song-bar {
        height: 150px;
        width: 80%;
        margin: 0 auto;
        .controls {
          display: flex;
          justify-content: space-around;
          align-items: center;
          margin-bottom: 40px;

          i.fa {
            color: #d3e7ee;
            cursor: pointer;
          }

          .previous > i.fa,
          .next > i.fa {
            font-size: 22px;
            opacity: 0.5;
          }

          .play > i.fa {
            font-size: 30px;
          }
        }

        div.time-wrapper {
          position: relative;

          span {
            color: white;
            font-size: 8px;
            position: absolute;
            top: -15px;

            &#current-time {
              left: 0;
            }
            &#total-time {
              right: 0;
            }
          }

          div.slider {
            background-color: #d3e7ee;
            border-radius: 2px;
            cursor: pointer;
            height: 2px;
            position: relative;
            width: 100%;

            div.progress {
              background-color: black;
              height: 100%;
              pointer-events: none;
              position: absolute;
              width: 0;

              div.pin {
                background-color: white;
                border-radius: 8px;
                height: 8px;
                position: absolute;
                pointer-events: all;
                right: -5px;
                top: -3px;
                width: 8px;
                box-shadow: 0px 1px 1px 0px rgba(0, 0, 0, 0.32);
                transition: transform 0.25s ease;

                &:active {
                  transform: scale(1.5);
                }
              }
            }
          }
        }
      }
    }
  }

  .spotify-link {
    text-align: center;
    margin-top: 50px;
    a {
      text-decoration: none;

      i {
        color: #7097a8;
        font-size: 50px;
      }
    }
  }
}
