body {
  min-height: 100vh;
  background-color: #d3e7ee;
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen,
    Ubuntu, Cantarell, "Open Sans", "Helvetica Neue", sans-serif;
  overflow: hidden;

  h1 {
    color: #7097a8;
    margin: 50px 0;
    text-align: center;
  }

  .player-wrapper {
    background-color: white;
    width: 280px;
    height: 500px;
    margin: 0 auto;
    padding: 5px;
    border-radius: 5px;

    p {
      margin: 0;
    }

    .player-inside-wrapper {
      background-color: #7097a8;
      border-radius: 5px;
      height: 100%;

      .song-image {
        padding: 20px;

        img {
          width: 100%;
          box-shadow: 0px 5px 25px rgb(50 50 50 / 50%);
        }
      }

      .player-artist-wrapper {
        text-align: center;
        margin-bottom: 30px;
        color: white;

        .song-name {
          font-size: 20px;
          font-weight: 700;
        }

        .artist-name {
          font-size: 16px;
        }
      }

      .song-bar {
        height: 150px;
        width: 80%;
        margin: 0 auto;
        .controls {
          display: flex;
          justify-content: space-around;
          align-items: center;
          margin-bottom: 40px;

          i.fa {
            color: #d3e7ee;
            cursor: pointer;
          }

          .previous > i.fa,
          .next > i.fa {
            font-size: 22px;
            opacity: 0.5;
          }

          .play > i.fa {
            font-size: 30px;
          }
        }

        div.time-wrapper {
          position: relative;

          span {
            color: white;
            font-size: 8px;
            position: absolute;
            top: -5px;

            &#current-time {
              left: 0;
            }
            &#total-time {
              right: 0;
            }
          }

          input[type="range"] {
            height: 2px;
            -webkit-appearance: none;
            width: 100%;
            margin: 0;
          }
          input[type="range"]:focus {
            outline: none;
          }
          input[type="range"]::-webkit-slider-runnable-track {
            width: 100%;
            height: 2px;
            cursor: pointer;
            background: white;
            border-radius: 5px;
          }
          input[type="range"]::-webkit-slider-thumb {
            height: 10px;
            width: 10px;
            border-radius: 50%;
            background: white;
            cursor: pointer;
            -webkit-appearance: none;
            margin-top: -4px;
          }

          // BROWSER: FIREFOX, EDGE
          //   input[type="range"]::-moz-range-track {
          //     width: 100%;
          //     height: 2px;
          //     cursor: pointer;
          //     background: white;
          //     border-radius: 5px;
          //   }
          //   input[type="range"]::-moz-range-thumb {
          //     box-shadow: 1px 1px 1px #000000;
          //     border: 1px solid #000000;
          //     height: 30px;
          //     width: 15px;
          //     border-radius: 5px;
          //     background: #ffffff;
          //     cursor: pointer;
          //   }
          //   input[type="range"]::-ms-track {
          //     width: 100%;
          //     height: 10px;
          //     cursor: pointer;
          //     animate: 0.2s;
          //     background: transparent;
          //     border-color: transparent;
          //     color: transparent;
          //   }
          //   input[type="range"]::-ms-fill-lower {
          //     background: #3071a9;
          //     border: 1px solid #000000;
          //     border-radius: 10px;
          //     box-shadow: 1px 1px 1px #000000;
          //   }
          //   input[type="range"]::-ms-fill-upper {
          //     background: #3071a9;
          //     border: 1px solid #000000;
          //     border-radius: 10px;
          //     box-shadow: 1px 1px 1px #000000;
          //   }
          //   input[type="range"]::-ms-thumb {
          //     margin-top: 1px;
          //     box-shadow: 1px 1px 1px #000000;
          //     border: 1px solid #000000;
          //     height: 30px;
          //     width: 15px;
          //     border-radius: 5px;
          //     background: #ffffff;
          //     cursor: pointer;
          //   }
          //   input[type="range"]:focus::-ms-fill-lower {
          //     background: #3071a9;
          //   }
          //   input[type="range"]:focus::-ms-fill-upper {
          //     background: #3071a9;
          //   }
        }
      }
    }
  }

  .spotify-link {
    text-align: center;
    margin-top: 50px;
    a {
      text-decoration: none;

      i {
        color: #7097a8;
        font-size: 50px;
      }
    }
  }
}
